      "regularPath": "/03.fun/03.%E9%9A%8F%E7%AC%94/59.%E8%AE%B0%E4%BA%8E%E4%B8%80%E4%B8%AA%E5%A4%B1%E7%9C%A0%E7%9A%84%E5%A4%9C.html",
      "relativePath": "03.fun/03.随笔/59.记于一个失眠的夜.md",
